import {
	Button,
	Container,
	Text,
	Title,
	Modal,
	TextInput,
	Group,
	Card,
	ActionIcon,
	Code,
	Avatar,
	Progress,
	Badge,
	Menu,
	UnstyledButton,
} from '@mantine/core';
import { useState, useRef, useEffect } from 'react';
import { MoonStars, Sun, Trash, Pencil, CalendarEvent, Check } from 'tabler-icons-react';

import {
	MantineProvider,
	ColorSchemeProvider,
	ColorScheme,
} from '@mantine/core';
import { useColorScheme } from '@mantine/hooks';
import { useHotkeys, useLocalStorage } from '@mantine/hooks';

export default function App() {
	const [tasks, setTasks] = useState([]);
	const [opened, setOpened] = useState(false);
	const [editMode, setEditMode] = useState(false);
	const [editIndex, setEditIndex] = useState(null);

	const preferredColorScheme = useColorScheme();
	const [colorScheme, setColorScheme] = useLocalStorage({
		key: 'mantine-color-scheme',
		defaultValue: 'light',
		getInitialValueInEffect: true,
	});
	const toggleColorScheme = value =>
		setColorScheme(value || (colorScheme === 'dark' ? 'light' : 'dark'));

	useHotkeys([['mod+J', () => toggleColorScheme()]]);

	const taskTitle = useRef('');
	const taskSummary = useRef('');
	const taskDeadline = useRef('');
	const taskProgress = useRef(0);

	function createTask() {
		setTasks([
			...tasks,
			{
				title: taskTitle.current.value,
				summary: taskSummary.current.value,
				deadline: taskDeadline.current.value,
				progress: taskProgress.current.value,
				done: false,
			},
		]);

		saveTasks([
			...tasks,
			{
				title: taskTitle.current.value,
				summary: taskSummary.current.value,
				deadline: taskDeadline.current.value,
				progress: taskProgress.current.value,
				done: false,
			},
		]);
	}

	function updateTask() {
		const updatedTasks = [...tasks];
		updatedTasks[editIndex] = {
			title: taskTitle.current.value,
			summary: taskSummary.current.value,
			deadline: taskDeadline.current.value,
			progress: taskProgress.current.value,
			done: updatedTasks[editIndex].done,
		};

		setTasks(updatedTasks);
		saveTasks(updatedTasks);
		setEditMode(false);
		setEditIndex(null);
	}

	function deleteTask(index) {
		var clonedTasks = [...tasks];

		clonedTasks.splice(index, 1);

		setTasks(clonedTasks);

		saveTasks([...clonedTasks]);
	}

	function markTaskAsDone(index) {
		const updatedTasks = [...tasks];
		updatedTasks[index].done = !updatedTasks[index].done;

		setTasks(updatedTasks);
		saveTasks(updatedTasks);
	}

	function loadTasks() {
		let loadedTasks = localStorage.getItem('tasks');

		let tasks = JSON.parse(loadedTasks);

		if (tasks) {
			setTasks(tasks);
		}
	}

	function saveTasks(tasks) {
		localStorage.setItem('tasks', JSON.stringify(tasks));
	}

	useEffect(() => {
		loadTasks();
	}, []);

	return (
		<ColorSchemeProvider
			colorScheme={colorScheme}
			toggleColorScheme={toggleColorScheme}>
			<MantineProvider
				theme={{ colorScheme, defaultRadius: 'md' }}
				withGlobalStyles
				withNormalizeCSS>
				<div className='App'>
					<Modal
						opened={opened}
						size={'md'}
						title={editMode ? 'Edit Task' : 'New Task'}
						withCloseButton={false}
						onClose={() => {
							setOpened(false);
							setEditMode(false);
							setEditIndex(null);
						}}
						centered>
						<TextInput
							mt={'md'}
							ref={taskTitle}
							placeholder={'Task Title'}
							required
							label={'Title'}
							defaultValue={
								editMode ? tasks[editIndex].title : ''
							}
						/>
						<TextInput
							ref={taskSummary}
							mt={'md'}
							placeholder={'Task Summary'}
							label={'Summary'}
							defaultValue={
								editMode ? tasks[editIndex].summary : ''
							}
						/>
						<TextInput
							ref={taskDeadline}
							mt={'md'}
							placeholder={'Task Deadline'}
							label={'Deadline'}
							type={'date'}
							defaultValue={
								editMode ? tasks[editIndex].deadline : ''
							}
						/>
						<TextInput
							ref={taskProgress}
							mt={'md'}
							placeholder={'Task Progress'}
							label={'Progress'}
							type={'number'}
							min={0}
							max={100}
							defaultValue={
								editMode ? tasks[editIndex].progress : 0
							}
						/>
						<Group mt={'md'} position={'apart'}>
							<Button
								onClick={() => {
									setOpened(false);
									setEditMode(false);
									setEditIndex(null);
								}}
								variant={'subtle'}>
								Cancel
							</Button>
							<Button
								onClick={() => {
									if (editMode) {
										updateTask();
									} else {
										createTask();
									}
									setOpened(false);
								}}>
								{editMode ? 'Update Task' : 'Create Task'}
							</Button>
						</Group>
					</Modal>
					<Container size={550} my={40}>
						<Group position={'apart'}>
							<Title
								sx={theme => ({
									fontFamily: `Greycliff CF, ${theme.fontFamily}`,
									fontWeight: 900,
								})}>
								My Tasks
							</Title>
							<ActionIcon
								color={'blue'}
								onClick={() => toggleColorScheme()}
								size='lg'>
								{colorScheme === 'dark' ? (
									<Sun size={16} />
								) : (
									<MoonStars size={16} />
								)}
							</ActionIcon>
						</Group>
						{tasks.length > 0 ? (
							tasks.map((task, index) => {
								if (task.title) {
									return (
										<Card
											withBorder
											key={index}
											mt={'sm'}
											sx={{
												opacity: task.done ? 0.5 : 1,
											}}>
											<Group position={'apart'}>
												<Avatar
													color={'blue'}
													radius={'xl'}>
													{task.title.charAt(0)}
												</Avatar>
												<div>
													<Text
														weight={'bold'}
														style={{
															textDecoration: task.done
																? 'line-through'
																: 'none',
														}}>
														{task.title}
													</Text>
													<Badge
														color={
															task.progress >= 75
																? 'teal'
																: task.progress >= 50
																	? 'yellow'
																	: 'red'
														}>
														{task.progress}%
													</Badge>
												</div>
												<Group>
													<ActionIcon
														onClick={() =>
															markTaskAsDone(index)
														}>
														<Check
															size={16}
															color={
																task.done
																	? 'teal'
																	: 'gray'
															}
														/>
													</ActionIcon>
													<Menu>
														<Menu.Target>
															<UnstyledButton>
																<ActionIcon>
																	<Pencil />
																</ActionIcon>
															</UnstyledButton>
														</Menu.Target>
														<Menu.Dropdown>
															<Menu.Item
																icon={
																	<Pencil size={14} />
																}
																onClick={() => {
																	setOpened(
																		true
																	);
																	setEditMode(
																		true
																	);
																	setEditIndex(
																		index
																	);
																	taskTitle.current.value =
																		task.title;
																	taskSummary.current.value =
																		task.summary;
																	taskDeadline.current.value =
																		task.deadline;
																	taskProgress.current.value =
																		task.progress;
																}}>
																Edit Task
															</Menu.Item>
															<Menu.Item
																color={'red'}
																icon={
																	<Trash size={14} />
																}
																onClick={() => {
																	deleteTask(index);
																}}>
																Delete Task
															</Menu.Item>
														</Menu.Dropdown>
													</Menu>
												</Group>
											</Group>
											<Text
												color={'dimmed'}
												size={'md'}
												mt={'sm'}>
												{task.summary
													? task.summary
													: 'No summary was provided for this task'}
											</Text>
											<Group mt={'sm'} noWrap>
												<CalendarEvent size={16} />
												<Text size={'sm'}>
													{task.deadline
														? `Deadline: ${task.deadline}`
														: 'No deadline set'}
												</Text>
											</Group>
											<Progress
												mt={'md'}
												value={task.progress}
												size={'lg'}
											/>
										</Card>
									);
								}
							})
						) : (
							<Text size={'lg'} mt={'md'} color={'dimmed'}>
								You have no tasks
							</Text>
						)}
						<Button
							onClick={() => {
								setOpened(true);
								setEditMode(false);
							}}
							fullWidth
							mt={'md'}>
							New Task
						</Button>
					</Container>
				</div>
			</MantineProvider>
		</ColorSchemeProvider>
	);
}
